import streamlit as st
import webbrowser

# Set the title of the Streamlit app
st.title("Apps")

# Create columns to arrange the app icons horizontally
col1, col2 = st.columns(2)

# Display Google Play Store icon in the first column
with col1:
    if st.button("Google Play Store"):
        # If the button is clicked, open the Google Play Store website
        webbrowser.open("https://play.google.com/store")
    st.image(
        "data:image/png;base64,",
        width=100,
    )

# Display Spotify icon in the second column
with col2:
    if st.button("Spotify"):
        # If the button is clicked, open the Spotify website
        webbrowser.open("https://www.spotify.com/")
    st.image(
        "https://upload.wikimedia.org/wikipedia/commons/1/19/Spotify_logo_without_text.svg",
        width=100,
    )
